import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Challenges from './pages/Challenges';

function App() {
  const [claim, setClaim] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState('');
  const [clarification, setClarification] = useState('');
  const [newInfo, setNewInfo] = useState('');

  const submitClaim = async () => {
    setError('');
    setResponse(null);

    try {
      const res = await fetch('http://localhost:4000/api/analyze-claim', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ claim })
      });

      const data = await res.json();

      if (data.error) {
        setError(data.error);
      } else {
        setResponse(data);
      }
    } catch (err) {
      setError('Something went wrong. Please try again.');
    }
  };

  const submitChallenge = async () => {
    try {
      await fetch('http://localhost:4000/api/challenge-claim', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ claim, clarification, newInfo })
      });
      alert('Challenge submitted!');
      setClarification('');
      setNewInfo('');
    } catch (err) {
      alert('Failed to submit challenge.');
    }
  };

  return (
    <Router>
      <div style={{ padding: 20, fontFamily: 'Arial' }}>
        <nav style={{ marginBottom: 20 }}>
          <Link to="/" style={{ marginRight: 10 }}>🏠 Home</Link>
          <Link to="/challenges">📂 View Challenges</Link>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div>
                <h1>🧠 TellTheTruth.ai</h1>
                <textarea
                  rows="4"
                  style={{ width: '100%' }}
                  placeholder="Enter a bold claim..."
                  value={claim}
                  onChange={(e) => setClaim(e.target.value)}
                />
                <button onClick={submitClaim} style={{ marginTop: 10 }}>
                  Analyze Claim
                </button>

                {error && <p style={{ color: 'red' }}>{error}</p>}

                {response && (
                  <div style={{ marginTop: 30 }}>
                    <h2>🧾 AI Analysis</h2>
                    <p><strong>Confidence:</strong> {response.confidence}%</p>
                    <p><strong>Summary:</strong> {response.summary}</p>

                    <h3>✅ Supporting Arguments</h3>
                    <ul>{response.supporting.map((item, i) => <li key={i}>{item}</li>)}</ul>

                    <h3>❌ Counterpoints</h3>
                    <ul>{response.counterpoints.map((item, i) => <li key={i}>{item}</li>)}</ul>

                    <h3>💣 TruthBombs</h3>
                    <ul>{response.TruthBombs?.map((item, i) => <li key={i}>{item}</li>)}</ul>

                    <h3>🔗 Sources</h3>
                    <ul>{response.sources.map((url, i) => (
                      <li key={i}>
                        <a href={url} target="_blank" rel="noreferrer">{url}</a>
                      </li>
                    ))}</ul>

                    <hr />
                    <h3>🗣️ Challenge this Answer</h3>
                    <textarea
                      placeholder="Clarify the original claim or critique the logic..."
                      value={clarification}
                      onChange={(e) => setClarification(e.target.value)}
                      rows="2"
                      style={{ width: '100%' }}
                    />
                    <textarea
                      placeholder="Add new information or evidence..."
                      value={newInfo}
                      onChange={(e) => setNewInfo(e.target.value)}
                      rows="2"
                      style={{ width: '100%', marginTop: 10 }}
                    />
                    <button onClick={submitChallenge} style={{ marginTop: 10 }}>
                      Submit Challenge
                    </button>
                  </div>
                )}
              </div>
            }
          />
          <Route path="/challenges" element={<Challenges />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
