// src/pages/Challenges.js
import React, { useEffect, useState } from 'react';

export default function Challenges() {
  const [challenges, setChallenges] = useState([]);

  useEffect(() => {
    fetch('/api/challenges')
      .then(res => res.json())
      .then(data => setChallenges(data))
      .catch(err => console.error('Error fetching challenges:', err));
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üóÇÔ∏è Community Challenges</h1>
      {challenges.length === 0 ? (
        <p>No challenges submitted yet.</p>
      ) : (
        challenges.map((item, index) => (
          <div key={index} style={{ borderBottom: '1px solid #ccc', padding: '1rem 0' }}>
            <strong>üì£ Claim:</strong> {item.claim}
            {item.clarification && (
              <div><strong>üìù Clarification:</strong> {item.clarification}</div>
            )}
            {item.newInfo && (
              <div><strong>üîç New Info:</strong> {item.newInfo}</div>
            )}
            <div><strong>üìÖ Timestamp:</strong> {new Date(item.timestamp).toLocaleString()}</div>
          </div>
        ))
      )}
    </div>
  );
}
