import React, { useState } from 'react';

function App() {
  const [messages, setMessages] = useState([
    {
      role: 'system',
      content: `You are an MQL4 coding expert. The user will describe a trading strategy in plain English, and your job is to output complete, clean MQL4 Expert Advisor (.mq4) code that matches the strategy.

Include:
- OnInit(), OnDeinit(), and OnTick()
- Risk management
- Comments in the code
- Only output code — no explanations, no markdown, no code blocks.`
    }
  ]);

  const [userInput, setUserInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [code, setCode] = useState('');
  const [error, setError] = useState('');

  const sendMessage = async (customInput = null) => {
    const input = customInput !== null ? customInput : userInput;
    if (!input.trim()) return;
    setLoading(true);
    setError('');

    const updatedMessages = [...messages, { role: 'user', content: input }];
    setMessages(updatedMessages);
    setUserInput('');

    try {
      const res = await fetch('http://localhost:4000/api/generate-ea', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ messages: updatedMessages })
      });

      const data = await res.json();

      if (data.error) {
        setError(data.error);
      } else {
        const assistantMsg = { role: 'assistant', content: data.eaCode };
        setMessages([...updatedMessages, assistantMsg]);
        setCode(data.eaCode);
      }
    } catch (err) {
      setError('Something went wrong.');
    } finally {
      setLoading(false);
    }
  };

  const resetSession = () => {
    setMessages([
      {
        role: 'system',
        content: `You are an MQL4 coding expert. The user will describe a trading strategy in plain English, and your job is to output complete, clean MQL4 Expert Advisor (.mq4) code that matches the strategy.

Include:
- OnInit(), OnDeinit(), and OnTick()
- Risk management
- Comments in the code
- Only output code — no explanations, no markdown, no code blocks.`
      }
    ]);
    setUserInput('');
    setCode('');
    setError('');
  };

  const downloadCode = () => {
    if (!code) return;
    const blob = new Blob([code], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'MyExpertAdvisor.mq4';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div style={{ padding: 20, fontFamily: 'Arial' }}>
      <h1>🤖 AI EA Builder</h1>

      <textarea
        rows="3"
        style={{ width: '100%' }}
        placeholder="Describe your EA strategy or enter a follow-up message..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />
      <div style={{ marginTop: 10 }}>
        <button onClick={() => sendMessage()} disabled={loading} style={{ marginRight: 10 }}>
          {loading ? 'Thinking...' : 'Send to AI'}
        </button>
        <button onClick={resetSession} style={{ marginRight: 10 }}>♻️ Reset Session</button>
        <button onClick={downloadCode} disabled={!code}>💾 Download EA</button>
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {code && (
        <div style={{ marginTop: 30 }}>
          <h2>📦 Current EA Code</h2>
          <pre
            style={{
              backgroundColor: '#000',
              color: '#0f0',
              padding: '1rem',
              borderRadius: 8,
              whiteSpace: 'pre-wrap',
              wordBreak: 'break-word',
              fontSize: '13px'
            }}
          >
            {code}
          </pre>
        </div>
      )}

      <div style={{ marginTop: 40 }}>
        <h3>🧪 Quick Start Examples</h3>
        <button
          onClick={() => sendMessage('Create an EA that buys when RSI is below 30 and MACD turns green. TP 30, SL 20.')}
          style={{ marginRight: 10 }}
        >
          RSI + MACD EA
        </button>
        <button
          onClick={() => sendMessage('Build an EA that opens a sell when a bearish engulfing candle forms on H1.')}
        >
          Candle Pattern EA
        </button>
      </div>
    </div>
  );
}

export default App;
