import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Challenges from './pages/Challenges';
import Responded from './pages/Responded';



function App() {
  const generateEA = async () => {
  setError('');
  setResponse(null);

  try {
    const res = await fetch('http://localhost:4000/api/generate-ea', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ claim })
    });

    const data = await res.json();

    if (data.error) {
      setError(data.error);
    } else {
      setResponse(data);
    }
  } catch (err) {
    setError('Something went wrong while generating the EA.');
  }
};


  const submitChallenge = async () => {
    try {
      await fetch('http://localhost:4000/api/challenge-claim', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ claim, clarification, newInfo })
      });
      alert('Challenge submitted!');
      setClarification('');
      setNewInfo('');
    } catch (err) {
      alert('Failed to submit challenge.');
    }
  };






  return (
    <Router>
      <div style={{ padding: 20, fontFamily: 'Arial' }}>
       <nav style={{ marginBottom: 20 }}>
  <Link to="/" style={{ marginRight: 10 }}>üè† Home</Link>
  <Link to="/challenges" style={{ marginRight: 10 }}>üìÇ View Challenges</Link>
  <Link to="/responded">üîÅ Updated Responses</Link>
</nav>
        <Routes>
          <Route
  path="/build-ea"
  element={
    <div>
      <h1>ü§ñ Build an EA with AI</h1>
      <textarea
        rows="4"
        style={{ width: '100%' }}
        placeholder="Describe your EA strategy here..."
        value={claim}
        onChange={(e) => setClaim(e.target.value)}
      />
      <button onClick={generateEA} style={{ marginTop: 10 }}>
        Generate EA Code
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {response && (
        <div style={{ marginTop: 30 }}>
          <h2>üì¶ Generated EA Code</h2>
          <pre
            style={{
              backgroundColor: '#111',
              color: '#0f0',
              padding: '1rem',
              borderRadius: 8,
              whiteSpace: 'pre-wrap',
              wordBreak: 'break-word'
            }}
          >
            {response.eaCode}
          </pre>
        </div>
      )}
    </div>
  }
/>

        </Routes>
      </div>
    </Router>
  );
}

export default App;
