import React, { useState } from 'react';

function App() {
  const [claim, setClaim] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState('');

  const generateEA = async () => {
    setError('');
    setResponse(null);

    try {
      const res = await fetch('http://localhost:4000/api/generate-ea', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ claim })
      });

      const data = await res.json();

      if (data.error) {
        setError(data.error);
      } else {
        setResponse(data);
      }
    } catch (err) {
      setError('Something went wrong while generating the EA.');
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: 'Arial' }}>
      <h1>ðŸ¤– Build Your Expert Advisor (EA)</h1>

      <textarea
        rows="4"
        style={{ width: '100%' }}
        placeholder="Describe your EA strategy here..."
        value={claim}
        onChange={(e) => setClaim(e.target.value)}
      />
      <button onClick={generateEA} style={{ marginTop: 10 }}>
        Generate EA Code
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {response && (
        <div style={{ marginTop: 30 }}>
          <h2>ðŸ“¦ Generated EA Code</h2>
          <pre
            style={{
              backgroundColor: '#111',
              color: '#0f0',
              padding: '1rem',
              borderRadius: 8,
              whiteSpace: 'pre-wrap',
              wordBreak: 'break-word'
            }}
          >
            {response.eaCode}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;
