// App.js - TellTheTruth.ai (React Frontend)

import React, { useState, useEffect } from 'react';

export default function App() {
  const [claim, setClaim] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [challenge, setChallenge] = useState({ clarification: '', newInfo: '' });
  const [challenges, setChallenges] = useState([]);

  const handleAnalyze = async () => {
    if (!claim.trim()) return;
    setLoading(true);
    setResult(null);
    try {
      const res = await fetch('/api/analyze-claim', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ claim })
      });
      const data = await res.json();
      setResult(data);
    } catch (err) {
      setResult({ error: 'Something went wrong. Please try again.' });
    }
    setLoading(false);
  };

  const handleChallenge = async () => {
    const { clarification, newInfo } = challenge;
    if (!clarification && !newInfo) return;
    await fetch('/api/challenge-claim', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ claim, clarification, newInfo })
    });
    setChallenges([...challenges, { clarification, newInfo }]);
    setChallenge({ clarification: '', newInfo: '' });
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-2xl mx-auto bg-white shadow-xl rounded-xl p-6 space-y-4">
        <h1 className="text-3xl font-bold text-center text-blue-700">TellTheTruth.ai</h1>

        <textarea
          className="w-full border border-gray-300 rounded p-3 text-lg"
          rows={4}
          value={claim}
          onChange={(e) => setClaim(e.target.value)}
          placeholder="e.g. The Earth is flat"
        />

        <button
          onClick={handleAnalyze}
          className="w-full bg-blue-600 text-white rounded-lg py-3 font-semibold hover:bg-blue-700"
        >
          {loading ? 'Analyzing...' : 'Analyze Claim'}
        </button>

        {result && !result.error && (
          <div className="space-y-4">
            <p><b>Confidence:</b> {result.confidence}%</p>
            <p><b>Summary:</b> {result.summary}</p>
            <p><b>Supporting:</b> <ul className="list-disc pl-6">{result.supporting.map((s, i) => <li key={i}>{s}</li>)}</ul></p>
            <p><b>Counterpoints:</b> <ul className="list-disc pl-6">{result.counterpoints.map((c, i) => <li key={i}>{c}</li>)}</ul></p>
            <p><b>Truth Bombs:</b> <ul className="list-disc pl-6">{result.TruthBombs?.map((t, i) => <li key={i}>{t}</li>)}</ul></p>
            <p><b>Sources:</b> <ul className="list-disc pl-6 text-sm text-blue-700">{result.sources.map((link, i) => <li key={i}><a href={link} target="_blank" rel="noopener noreferrer">{link}</a></li>)}</ul></p>
          </div>
        )}

        {result?.error && <p className="text-red-600">{result.error}</p>}

        {result && (
          <div className="mt-6">
            <h2 className="text-xl font-bold">Challenge or Clarify:</h2>
            <textarea
              className="w-full border mt-2 p-2"
              rows={3}
              placeholder="Clarify something in the original claim"
              value={challenge.clarification}
              onChange={(e) => setChallenge({ ...challenge, clarification: e.target.value })}
            />
            <textarea
              className="w-full border mt-2 p-2"
              rows={3}
              placeholder="Provide new information not listed in the response"
              value={challenge.newInfo}
              onChange={(e) => setChallenge({ ...challenge, newInfo: e.target.value })}
            />
            <button
              onClick={handleChallenge}
              className="mt-2 bg-yellow-600 text-white px-4 py-2 rounded hover:bg-yellow-700"
            >
              Submit Challenge
            </button>

            {challenges.length > 0 && (
              <div className="mt-4">
                <h3 className="font-bold">Submitted Challenges:</h3>
                <ul className="list-disc pl-6">
                  {challenges.map((c, i) => (
                    <li key={i}>{c.clarification || c.newInfo}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
