import React, { useState } from 'react';

export default function TellTheTruthApp() {
  const [claim, setClaim] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  const handleSubmit = async () => {
    if (!claim.trim()) return;
    setLoading(true);
    setResult(null);

    try {
      const response = await fetch('http://localhost:4000/api/analyze-claim', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ claim })
      });

      if (!response.ok) {
        const text = await response.text();
        throw new Error(`Server responded with status ${response.status}: ${text}`);
      }

      const data = await response.json();
      setResult(data);
    } catch (err) {
      console.error('Error analyzing claim:', err.message || err);
      setResult({ error: 'Something went wrong. Please try again.' });
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-2xl mx-auto bg-white shadow-2xl rounded-2xl p-6 space-y-4">
        <h1 className="text-3xl font-bold text-center text-blue-700">TellTheTruth.ai</h1>
        <p className="text-center text-gray-500">Enter any claim below. We'll show you the context, counterpoints, and source-backed insights.</p>

        <textarea
          className="w-full border border-gray-300 rounded p-3 text-lg"
          rows={4}
          value={claim}
          onChange={(e) => setClaim(e.target.value)}
          placeholder="e.g. The moon landing was faked"
        />

        <button
          onClick={handleSubmit}
          className="w-full bg-blue-600 text-white rounded-xl py-3 font-semibold hover:bg-blue-700 transition duration-200"
        >
          {loading ? 'Analyzing...' : 'Analyze Claim'}
        </button>

        {result && (
          <div className="mt-6 space-y-4">
            {result.error && <p className="text-red-600 text-center">{result.error}</p>}

            {result.confidence && (
              <div className="bg-blue-50 border-l-4 border-blue-500 p-4">
                <p className="font-semibold">Confidence:</p>
                <p>{result.confidence}% — {result.summary}</p>
              </div>
            )}

            {result.supporting && (
              <div className="bg-green-50 border-l-4 border-green-500 p-4">
                <p className="font-semibold">Supporting Evidence:</p>
                <ul className="list-disc list-inside">
                  {result.supporting.map((item, idx) => (
                    <li key={idx}>{item}</li>
                  ))}
                </ul>
              </div>
            )}

            {result.counterpoints && (
              <div className="bg-yellow-50 border-l-4 border-yellow-500 p-4">
                <p className="font-semibold">Counterpoints:</p>
                <ul className="list-disc list-inside">
                  {result.counterpoints.map((item, idx) => (
                    <li key={idx}>{item}</li>
                  ))}
                </ul>
              </div>
            )}

            {result.sources && (
              <div className="bg-gray-50 border-l-4 border-gray-400 p-4">
                <p className="font-semibold">Sources:</p>
                <ul className="list-disc list-inside text-sm text-blue-700">
                  {result.sources.map((link, idx) => (
                    <li key={idx}><a href={link} target="_blank" rel="noopener noreferrer">{link}</a></li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
